import { Paper, Text, Stack, Group, Progress, RingProgress, Button } from '@mantine/core';
import { IconBolt, IconLeaf, IconCurrencyDollar, IconTrendingUp } from '@tabler/icons-react';

// Energy Status Card - Generated by AI
export function EnergyStatusCard({ data }: { data: any }) {
  return (
    <Paper p="md" style={{ background: '#0f1012', border: '1px solid #2C2E33' }}>
      <Stack gap="md">
        <Text size="sm" fw={600} c="white">
          Current Energy Status
        </Text>
        <Group grow>
          <Stack gap={4}>
            <Group gap={4}>
              <IconBolt size={16} color="#2F9E44" />
              <Text size="xs" c="dimmed">Consumption</Text>
            </Group>
            <Text size="xl" fw={700} c="white">
              {data.consumption} kWh
            </Text>
          </Stack>
          <Stack gap={4}>
            <Group gap={4}>
              <IconTrendingUp size={16} color="#2F9E44" />
              <Text size="xs" c="dimmed">Efficiency</Text>
            </Group>
            <Text size="xl" fw={700} c="#2F9E44">
              {data.efficiency}%
            </Text>
          </Stack>
        </Group>
        <Progress value={data.efficiency} color="green" size="md" />
      </Stack>
    </Paper>
  );
}

