// Carbon Impact Card - Generated by AI
export function CarbonImpactCard({ data }: { data: any }) {
  return (
    <Paper p="md" style={{ background: '#0f1012', border: '1px solid #2C2E33' }}>
      <Stack gap="md">
        <Group gap={4}>
          <IconLeaf size={20} color="#2F9E44" />
          <Text size="sm" fw={600} c="white">
            Carbon Reduction
          </Text>
        </Group>
        <Group justify="center">
          <RingProgress
            size={120}
            thickness={12}
            sections={[{ value: (data.reduced / data.baseline) * 100, color: 'green' }]}
            label={
              <Stack gap={0} align="center">
                <Text size="xl" fw={700} c="white" lh={1}>
                  {data.reduced}
                </Text>
                <Text size="xs" c="dimmed">
                  kg COâ‚‚
                </Text>
              </Stack>
            }
          />
        </Group>
        <Text size="xs" c="dimmed" ta="center">
          Equivalent to {data.carKm} km of driving avoided
        </Text>
      </Stack>
    </Paper>
  );
}

