// Quick Metrics Grid - Generated by AI
export function QuickMetricsGrid({ data }: { data: any }) {
  return (
    <Stack gap="xs">
      {data.metrics.map((metric: any, i: number) => (
        <Group key={i} justify="space-between" p="sm" style={{ background: '#0f1012', borderRadius: 4 }}>
          <Text size="sm" c="dimmed">{metric.label}</Text>
          <Text size="sm" fw={700} c={metric.positive ? '#2F9E44' : 'white'}>
            {metric.value}
          </Text>
        </Group>
      ))}
    </Stack>
  );
}
